mapper.select:
select *
from members
where user_email = #{userId} and user_password = #{userPwd}

select user_password
from members
where user_email = #{userId} and user_name = #{userName}

mapper.update:
update members
set user_name = #{userName}, user_password = #{userPwd}
where user_email = #{userId}

mapper.select:
select count(*)
from members
where user_email = #{userId}
